class Ajax
{constructor()
{}
post(oData,sUrl,iTimeout=8000)
{return new Promise((resolve,reject)=>{this.request({method:'POST',url:sUrl,data:oData}).then((oResponse)=>{resolve(oResponse);}).catch((oResponse)=>{reject(oResponse);});},iTimeout);}
get(oData,sUrl,iTimeout=8000)
{return new Promise((resolve,reject)=>{this.request({method:'GET',url:sUrl,data:oData}).then((oResponse)=>{resolve(oResponse);}).catch((oResponse)=>{reject(oResponse);});},iTimeout);}
request(oParams,iTimeOut=8000)
{return new Promise((resolve,reject)=>{var timeout=()=>{reject('Request Timeout');}
setTimeout(timeout,iTimeOut);$.ajax({method:oParams.method,url:oParams.url,data:oParams.data,dataType:'JSON',success:(oResponse)=>{clearTimeout(timeout);resolve(oResponse);}}).fail((e)=>{reject(e);});});}};$(document).ready(()=>{var body=$('body');var username=$('.username');var password=$('.password');var hash=$('.hash');;const ajax=new Ajax();$('#login_form').validator('update').on('submit',(e)=>{if(e.isDefaultPrevented()){}else{e.preventDefault();ajax.get({username:username.val(),password:password.val()},'/login/rest/'+hash.val()).then((oResponse)=>{console.log(oResponse);}).catch((oResponse)=>{console.log(oResponse);});}});});